import * as React from "react";
import Typography from "@mui/material/Typography";
import { useNavigate } from "react-router-dom";
import Button from "@mui/material/Button";
import Chip from "@mui/material/Chip";
import Box from "@mui/material/Box";

export default function JobCard({ job }) {
  const navigate = useNavigate();
  const skillsArr = job.skills;
  return (
    <Box sx={{ p: 2, border: "1px #dff9fb grey", bgcolor: "#f7d794" }}>
      <Typography gutterBottom variant="h5" component="div">
        {job.title}
      </Typography>
      <Typography spacing={5}>
        {skillsArr.map((skill) => (
          <Chip label={skill} color="success" sx={{ p: 2 }} />
        ))}
      </Typography>

      <Typography variant="body2" color="text.secondary">
        {job.city} - {job.description}
      </Typography>
      <Button variant="contained" onClick={() => navigate(`/book/${job.id}`)}>
        LEARN MORE
      </Button>
    </Box>
  );
}
