import React from "react";
import useMusicPlayer from "../hooks/usePlayerProvider";

function Header() {
  const { currentTrackName } = useMusicPlayer();
  return (
    <div className="header">
      <p>Song</p>
      <marquee>{currentTrackName}</marquee>
    </div>
  );
}

export default Header;
